package app

import (
	"errors"
	"fmt"
	"log"
	"oneinstack/internal/models"
	"oneinstack/utils"

	"github.com/glebarez/sqlite"
	"gorm.io/gorm"
	"gorm.io/gorm/logger"
)

func init() {
	utils.EnsureOneDir() // 新增目录检查
	if err := InitDB(GetBasePath() + "myadmin.db"); err != nil {
		log.Fatal("InitDB error:", err)
	}
}

func InitDB(dbPath string) error {
	gc := &gorm.Config{}
	gc.Logger = logger.Default.LogMode(logger.Error)
	if ENV == "debug" {
		gc.Logger = logger.Default.LogMode(logger.Info)
	}

	d, err := gorm.Open(sqlite.Open(dbPath))
	if err != nil {
		return err
	}
	db = d
	// 检查是否存在用户，如果不存在提示创建管理员
	err = createTables()
	if err != nil {
		log.Fatal("failed to migrate the database:", err)
	}

	return nil
}

func createTables() error {
	err := db.AutoMigrate(&models.System{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.User{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.Storage{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.Library{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.Software{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.Website{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.Remark{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.Dictionary{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.IptablesRule{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.CronJob{})
	if err != nil {
		return err
	}
	err = db.AutoMigrate(&models.JobExecution{})
	if err != nil {
		return err
	}
	err = initSoftware()
	if err != nil {
		return err
	}
	err = initDic()
	if err != nil {
		return err
	}
	err = initRemark()
	if err != nil {
		return err
	}
	err = InitSystem()
	if err != nil {
		return err
	}
	// 登录日志
	err = db.AutoMigrate(&models.SystemLog{})
	if err != nil {
		return err
	}
	return nil
}

