func initDic() error {
	r := []*models.Dictionary{{
		Key:   "数据库",
		Value: "数据库",
		Q:     "soft_tags",
	},
		{
			Key:   "缓存",
			Value: "缓存",
			Q:     "soft_tags",
		},
		{
			Key:   "web服务",
			Value: "web服务",
			Q:     "soft_tags",
		},
		{
			Key:   "php",
			Value: "php",
			Q:     "soft_tags",
		}}
	var dic models.Dictionary
	result := db.First(&dic)
	if result.Error != nil && !errors.Is(result.Error, gorm.ErrRecordNotFound) {
		return result.Error
	}
	if dic.ID > 0 {
		return nil
	}
	tx := db.CreateInBatches(r, len(r))
	return tx.Error
}

func initRemark() error {
	r := &models.Remark{
		Content: "",
	}
	result := db.First(r)
	if result.Error != nil && !errors.Is(result.Error, gorm.ErrRecordNotFound) {
		return result.Error
	}
	if r.ID > 0 {
		return nil
	}
	tx := db.Create(r)
	return tx.Error
}

func InitUser(userName string, password string) error {
	var count int64 = 0
	tx := DB().Model(models.User{}).Count(&count)
	if tx.Error != nil {
		return tx.Error
	}
	if count > 0 {
		return nil
	}
	err := setupAdminUser(userName, password)
	if err != nil {
		return err
	}
	return nil
}

func setupAdminUser(userName string, password string) error {
	hashed, err := utils.HashPassword(password)
	if err != nil {
		return err
	}
	user := &models.User{
		Username: userName,
		Password: hashed,
		IsAdmin:  true,
	}
	tx := DB().Create(user)
	if tx.Error != nil {
		return tx.Error
	}
	fmt.Printf("用户创建成功.\n用户名: %s\n用户密码: %s\n", userName, password)
	return nil
}

func getAdminUser() error {
	var user models.User
	tx := DB().First(&user)
	if tx.Error != nil {
		return tx.Error
	}
	fmt.Printf("用户创建成功.\n用户名: %s\n用户密码: %s\n", user.Username, user.Password)
	return nil
}

func InitSystem() error {
	r := &models.System{
		Title: "OneStack",
	}
	var count int64 = 0
	tx := DB().Model(models.System{}).Count(&count)
	if tx.Error != nil {
		return tx.Error
	}
	if count > 0 {
		return nil
	}
	tx = DB().Create(r)
	return tx.Error
}
